import React, { useReducer } from "react";
import DigitButton from "./digitButton";
import Oprbtn from "./operandButton";




export const ACTIONS ={
ADD:"ADD",
OPERATION:"OPERATION",
CLEAR:"CLEAR",
DEL:"DEL",
EVAL:"EVAL"
}


const initialState = {
    current:"0",
    previous:"",
    operation:""
}

const reducer = (state,{type,payload}) =>{
    switch(type){
        case ACTIONS.ADD:
            if(state.current ==="0" ){
                if(payload.value ==="0"){
                     return state
                }
                else{
                    return {
                        ...state,
                        current:payload.value
                    }
                }
              
            }
           if(payload.value ==="." && state.current.includes(".")){
               return state
           }
        return{
            ...state,
            current:`${state.current}${payload.value}`
        }
    case ACTIONS.CLEAR:
        return initialState  
    case ACTIONS.DEL:
        return {
            ...state,
            current:state.current.slice(0,-1)
        }      
    case ACTIONS.OPERATION:
        if(state.previous === ""){
            return{
                ...state,
                current:"0",
                operation:payload.operation,
                previous:state.current
            }
        }
        return{
    ...state,
    current:"0",
    previous:evaluate(state)
        }   
}
}
function evaluate({current,previous,operation}){
let curr = parseFloat(current)
let prev = parseFloat(previous)
if(isNaN(curr)&&isNaN(prev)){
    return ""
}
switch(operation){
    case "+":
        return ''
    case "-":
        return prev - curr
    case "/":
        return prev / curr
    case "*":
        return prev * curr
}
}
export default function App(){
    let [{current,previous,operation},dispatch] = useReducer(reducer,initialState)
    return(
        <div className="container">
            <header>Calculator</header>
            <div className="calculator-grid">
           <div className="output">
               <div className="previous">{previous}{operation}</div>
               <div className="current">{current}</div>
           </div>
           <button className="span-two danger" onClick={()=>dispatch({type:ACTIONS.CLEAR})}>AC</button>
           <button className="danger" onClick={()=>dispatch({type:ACTIONS.DEL})}>C</button>
           <Oprbtn dispatch={dispatch} operation={"*"}/>
           <DigitButton value={"1"} dispatch ={dispatch}/>
           <DigitButton value={"2"} dispatch ={dispatch}/>
           <DigitButton value={"3"} dispatch ={dispatch}/>
           <Oprbtn dispatch={dispatch} operation={"/"}/>
           <DigitButton value={"4"} dispatch ={dispatch}/>
           <DigitButton value={"5"} dispatch ={dispatch}/>
           <DigitButton value={"6"} dispatch ={dispatch}/>
           <Oprbtn dispatch={dispatch} operation={"+"}/>
           <DigitButton value={"7"} dispatch ={dispatch}/>
           <DigitButton value={"8"} dispatch ={dispatch}/>
           <DigitButton value={"9"} dispatch ={dispatch}/>
           <Oprbtn dispatch={dispatch} operation={"-"}/>
           <DigitButton value={'.'} dispatch ={dispatch} />
           <DigitButton value={"0"} dispatch ={dispatch}/>
           <button className="span-two">=</button>
            </div>
            <footer>Copyrights&copy; Cybug Technologies</footer>
        </div>
    )
}
